import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [question, setQuestion] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const validateQuestion = async () => {
    if (!question.trim()) return;

    setLoading(true);
    setError('');

    try {
      const response = await axios.post('/api/validate', {
        question: question
      });
      setResult(response.data);
    } catch (err) {
      setError('Error processing question: ' + (err.response?.data?.error || err.message));
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status?.toUpperCase()) {
      case 'VALID': return '#28a745';
      case 'WARNING': return '#ffc107';
      case 'REJECTED': return '#dc3545';
      default: return '#6c757d';
    }
  };

  const getValidationIcon = (method) => {
    return method === 'FLAN-T5' ? 'ü§ñ' : 'üìä';
  };

  return (
    <div className="App">
      <header className="header">
        <div className="header-content">
          <h1>Academic Doubt Clarification System</h1>
          <p>RAG-based Computer Networks Question Validation & Answer Generation</p>
          <div className="status-indicator">
            <span className="status-dot"></span>
            System Online
          </div>
        </div>
      </header>

      <main className="main-content">
        <div className="input-section">
          <h2>Question Validation</h2>
          <div className="input-group">
            <input
              type="text"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="Enter your Computer Networks question..."
              className="question-input"
              onKeyPress={(e) => e.key === 'Enter' && validateQuestion()}
            />
            <button 
              onClick={validateQuestion} 
              disabled={loading || !question.trim()}
              className="validate-btn"
            >
              {loading ? 'Processing...' : 'Validate'}
            </button>
          </div>
        </div>

        {error && (
          <div className="error-message">
            ‚ùå {error}
          </div>
        )}

        {result && (
          <div className="results-section">
            <div className="results-grid">
              <div className="result-card">
                <h3>Question Analysis</h3>
                <div className="analysis-item">
                  <strong>Original:</strong> {result.original_question}
                </div>
                <div className="analysis-item">
                  <strong>Processed:</strong> {result.corrected_question}
                </div>
                {result.corrections_applied && result.corrections_applied.length > 0 && (
                  <div className="analysis-item">
                    <strong>Corrections:</strong> {result.corrections_applied.join(', ')}
                  </div>
                )}
                <div className="status-badge" style={{ backgroundColor: getStatusColor(result.status) }}>
                  STATUS: {result.status?.toUpperCase()}
                </div>
                <div className="score">
                  <strong>Score:</strong> {result.final_score?.toFixed(2)}
                </div>
              </div>

              <div className="result-card">
                <h3>Validation Details</h3>
                {result.validation_method && (
                  <div className="validation-method">
                    {getValidationIcon(result.validation_method)} <strong>Method:</strong> {result.validation_method}
                    {result.validation_method === 'FLAN-T5' ? ' (Contextual AI)' : ' (Similarity)'}
                  </div>
                )}

                {result.relevance_details && (
                  <div className="relevance-details">
                    <div><strong>Detection:</strong> {result.relevance_details.method}</div>
                    {result.relevance_details.reason && (
                      <div><strong>Analysis:</strong> {result.relevance_details.reason}</div>
                    )}
                  </div>
                )}

                {result.component_scores && (
                  <div className="component-scores">
                    <h4>Component Scores</h4>
                    <div className="score-item">
                      Semantic Sanity: {result.component_scores.semantic_sanity?.toFixed(2)}
                    </div>
                    <div className="score-item">
                      Syllabus Relevance: {result.component_scores.syllabus_relevance?.toFixed(2)}
                    </div>
                  </div>
                )}
              </div>
            </div>

            {result.feedback && (
              <div className="feedback-section">
                <h3>System Feedback</h3>
                <div className="feedback-content">
                  <div className="feedback-summary">
                    <strong>Summary:</strong> {result.feedback.summary}
                  </div>

                  {result.feedback.issues && result.feedback.issues.length > 0 && (
                    <div className="feedback-issues">
                      <strong>Issues:</strong> {result.feedback.issues.join('; ')}
                    </div>
                  )}

                  {result.feedback.suggestions && result.feedback.suggestions.filter(s => s).length > 0 && (
                    <div className="feedback-suggestions">
                      <strong>Suggestions:</strong> {result.feedback.suggestions.filter(s => s).join('; ')}
                    </div>
                  )}

                  {result.feedback.strengths && result.feedback.strengths.length > 0 && (
                    <div className="feedback-strengths">
                      <strong>Strengths:</strong> {result.feedback.strengths.join('; ')}
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>
        )}

        <div className="system-info">
          <div className="info-card">
            <h3>Academic Doubt Clarification System</h3>
            <p>Intelligent question validation for Computer Networks using fine-tuned FLAN-T5 and semantic analysis</p>
            <div className="features">
              <div className="feature">‚úì Real-time ML validation</div>
              <div className="feature">‚úì Context-aware analysis</div>
              <div className="feature">‚úì Syllabus alignment checking</div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
